import React, { useEffect, useRef, useState, useCallback } from "react";
import Map from "ol/Map";
import View from "ol/View";
import { Tile as TileLayer, Vector as VectorLayer } from "ol/layer";
import { OSM } from "ol/source";
import { Vector as VectorSource } from "ol/source";
import { Draw } from "ol/interaction";
import { Fill, Stroke, Style } from "ol/style";
import { fromLonLat } from "ol/proj";

function App() {
  const mapRef = useRef(null);
  const vectorSourceRef = useRef(new VectorSource());
  const drawRef = useRef(null);
  const [map, setMap] = useState(null);
  const [polygonDrawn, setPolygonDrawn] = useState(false);

  useEffect(() => {
    // Initialize the OpenLayers map
    const initialMap = new Map({
      target: mapRef.current,
      layers: [
        new TileLayer({ source: new OSM() }),
        new VectorLayer({
          source: vectorSourceRef.current,
          style: new Style({
            stroke: new Stroke({ color: "blue", width: 2 }),
            fill: new Fill({ color: "rgba(0, 0, 255, 0.1)" }),
          }),
        }),
      ],
      view: new View({
        center: fromLonLat([36.8219, -1.2921]), // Nairobi, Kenya
        zoom: 12,
      }),
    });

    setMap(initialMap);
    return () => initialMap.setTarget(null); // Cleanup on unmount
  }, []);

  const startDrawing = useCallback(() => {
    if (!map || polygonDrawn) return;

    drawRef.current = new Draw({
      source: vectorSourceRef.current,
      type: "Polygon",
    });

    map.addInteraction(drawRef.current);

    drawRef.current.on("drawend", (event) => {
      console.log("Polygon Drawn:", event.feature.getGeometry().getCoordinates());
      setPolygonDrawn(true);
      map.removeInteraction(drawRef.current);
    });
  }, [map, polygonDrawn]);

  const finalizePolygon = useCallback(() => {
    if (!polygonDrawn) {
      alert("No polygon drawn yet!");
      return;
    }
    console.log("Finalizing Polygon...");
    // Logic to finalize or process the drawn polygon
  }, [polygonDrawn]);

  return (
    <div style={{ width: "100vw", height: "100vh" }}>
      <div ref={mapRef} style={{ width: "100%", height: "90%" }}></div>
      <div style={{ textAlign: "center", padding: "10px" }}>
        <button onClick={startDrawing} disabled={polygonDrawn} style={{ marginRight: "10px" }}>
          Start Drawing
        </button>
        <button onClick={finalizePolygon} disabled={!polygonDrawn}>
          Finalize Polygon
        </button>
      </div>
    </div>
  );
}

export default App;
